package basicjava;

public class DataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//syntax for datatype variablename = "value";
		int number =12;
		System.out.println("value is " + number);//+ wired operator
		
		byte bitNumber =127;
		System.out.println("byte value is "+ bitNumber);
		
		short shortValue =3898;
		System.out.println("short value is " + shortValue);
		
		long valueLong = 12389076780L;
		System.out.println("long value is " +valueLong);
		
		float marks = 42.3f;
		System.out.println("marks of student :"+ marks);
		
		
		
		

	}

}
