public class SumofEvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter the number:");
		int num=sc.nextInt();
		sumEvenOdd(num);

	}

	public static void sumEvenOdd(int num) {
		// TODO Auto-generated method stub
		if(num%2==0) {
			System.out.println();
		}
		
	}

}
